package gyroorm.persist;import gyroorm.GyroORMException;public class DataPersisterException extends GyroORMException {	public DataPersisterException() {		super();	}	public DataPersisterException(String message) {		super(message);	}	public DataPersisterException(String message, Throwable th) {		super(message, th);	}	public DataPersisterException(Throwable th) {  	super(th);	}}